mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status},
where id = #{id}

mapper.select:
select dish.*, c.name category_name
from dish
left join category c on dish.category_id = c.id

    dish.name like CONCAT('%', #{name}, '%')
    and dish.category_id = #{categoryId}
    and dish.status = #{status}
