mapper.update:
update setmeal
name = #{name}, price = #{price}, description = #{description}, category_id = #{categoryId}, status = #{status}, image = #{image},
where id = #{id};

mapper.delete:
delete
from setmeal
where id in

    #{id}
 ;

mapper.select:
select s.*, c.name category_name from setmeal s left join category c on s.category_id = c.id

    s.name like CONCAT('%', #{name}, '%')
    and s.category_id = #{categoryId}
    and s.status = #{status}
